	<script>
	(function($, undefined){
		'use strict';

		function init(){
			$('#interests ul').children().each(function(index, elem){
				$(elem).on('click', function(){
					var idName = $(this).attr('id');
					$('#interests ul li').removeClass('active');
					$(this).addClass('active');
					$('#container-end').find('.showing').fadeOut().removeClass('showing');
					$('#container-end').find('.' + idName).delay( 400 ).fadeIn().addClass('showing');
				});
			});
		}
		$(init);
	}(jQuery));
	</script>
